mapper.select:
select er.* from event_record${tenantSuffix} er
where 1 = 1

    AND er.event_type = #{param.eventType}


    AND er.event_timestamp >= #{param.timeRange.startTime}


    AND er.event_timestamp <= #{param.timeRange.startTime}



    order by er.event_timestamp asc


    order by er.event_timestamp desc
